fun toIcon(sc: SuccessCriterion): String = when (sc.status) {
    Status.COMPLIANT -> compliant(sc.description, sc.number)
    Status.NON_COMPLIANT -> nonCompliant(sc.description, sc.number)
    Status.NOT_APPLICABLE -> tooHard(sc.description, sc.number)
    Status.NOT_TESTED -> notTested(sc.description, sc.number)
}